    IF (((OLD.genbank_id IS NULL) AND (NEW.genbank_id IS NOT NULL)) OR ((OLD.genbank_id IS NOT NULL) AND (NEW.genbank_id IS NULL)) OR (OLD.genbank_id != NEW.genbank_id)) THEN
