    RETURN NEW;

  ELSIF (TG_OP = 'DELETE') THEN

    v_row := OLD.dbentity_id || '[:]' ||
             OLD.taxonomy_id || '[:]' || OLD.strain_type || '[:]' ||
             coalesce(OLD.genotype,'') || '[:]' || coalesce(OLD.genbank_id,'') || '[:]' ||
